import { Component, OnInit } from '@angular/core';
import { Router } from '@angular/router';
import { ToastrService } from 'ngx-toastr';
import { Loginintoapp } from 'src/app/models/loginintoapp';
import { AuthService } from 'src/app/service/auth.service';
import { TokenService } from 'src/app/service/token.service';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent implements OnInit {

  username!: string;
  password!: string;

  constructor(
    private authService: AuthService,
    private tokenService: TokenService,
    private toastrService: ToastrService,
    private router: Router
    ) {}

  ngOnInit(): void {

  }

  onLogin(): void {
    const dto = new Loginintoapp(this.username, this.password);
    this.authService.login(dto).subscribe(
      data => {
        this.tokenService.logout();
        this.tokenService.setToken(data.token);
        this.router.navigate(['/home']);
      }, err => {
        this.toastrService.error('Error iniciando sesion', 'Error', {
          timeOut: 3000, positionClass: 'toast-top-center'
        });
      }
    );
  }

}
<div class="container d-flex justify-content-center" style="margin-top: 10%;">
    <div class="card">
        <div class="card-body">
            <form #f="ngForm" (ngSubmit)="onLogin()" novalidate>
                <div class="form-group">
                    <label for="username">Nombre de Usuario</label>
                    <input type="text" name="username" id="username" class="form-control"
                        [(ngModel)]="username" required>
                </div><br>
                <div class="form-group">
                    <label for="password" >Contraseña</label>
                    <input type="password" name="password" id="password" class="form-control" [(ngModel)]="password"
                        required>
                </div><br>
                <div class="form-group" style="text-align: center">
                    <button class="btn btn-outline-success btn-block" [disabled]="!f.valid" type="submit">
                        <i class="fas fa-sign-in-alt"></i> Iniciar Sesión</button>
                </div>
            </form>
        </div>
    </div>
</div>
